<?php
namespace ifeiwu; class Config { private static $config = array(); public static function get($name) { if (!isset(self::$config[$name])) { $file = APP_PATH . 'config/' . $name . '.php'; if (file_exists($file)) { self::$config[$name] = include $file; } else { self::$config[$name] = array(); } } if (strpos($name, '.')) { list($name1, $name2) = explode('.', $name); $value = isset(self::$config[$name1][$name2]) ? self::$config[$name1][$name2] : null; } else { $value = isset(self::$config[$name]) ? self::$config[$name] : null; } return $value; } public static function set($name, $value) { if (strpos($name, '.')) { list($name1, $name2) = explode('.', $name); self::$config[$name1][$name2] = $value; } else { self::$config[$name] = $value; } } } 