<?php
namespace ifeiwu; class Controller { protected $view; public function __construct($request, $response) { $this->view = new View($request, $response); } public function theme($name) { $this->view->theme($name); } public function layout($name = false, $theme = '') { $this->view->layout($name, $theme); } public function assign($name, $value = '') { return $this->view->assign($name, $value); } public function fetch($template, $vars = array()) { return $this->view->fetch($template, $vars); } public function display($name = '', $vars = array()) { $this->view->display($name, $vars); } } 